import React from "react";
import { styled } from "@mui/material/styles";
import {
  Drawer,
  List,
  ListItem,
  ListItemIcon,
  ListItemText,
  Box,
  Typography,
  Tooltip,
} from "@mui/material";
import { useNavigate, useLocation } from "react-router-dom";
import { tokens } from "../../theme/theme";
import { useAuth } from "../../context/AuthContext";
import { getMenuItemsForRole, UserRole } from "../../constants/roles";
import { getIconComponent } from "../../utils/iconMapper";

const DRAWER_WIDTH = 280;

const StyledDrawer = styled(Drawer)(({ theme }) => ({
  width: DRAWER_WIDTH,
  flexShrink: 0,
  "& .MuiDrawer-paper": {
    width: DRAWER_WIDTH,
    boxSizing: "border-box",
    background: `linear-gradient(180deg, 
      ${tokens.primary.main} 0%, 
      ${tokens.primary.light} 50%, 
      ${tokens.secondary.main} 100%)`,
    color: theme.palette.common.white,
    borderRight: "none",
  },
}));

const StyledListItem = styled(ListItem)<{ active?: number }>(({ active }) => ({
  margin: "8px 16px",
  borderRadius: "12px",
  backgroundColor: active ? "rgba(255, 255, 255, 0.1)" : "transparent",
  "&:hover": {
    backgroundColor: "rgba(255, 255, 255, 0.2)",
  },
  cursor: "pointer",
}));

const Logo = styled(Box)({
  display: "flex",
  alignItems: "center",
  justifyContent: "center",
  padding: "24px 16px",
  "& img": {
    width: "120px",
    height: "auto",
  },
});

interface SidebarProps {
  open: boolean;
  collapsed: boolean;
  onClose: () => void;
}

const SidebarBox = styled(Box)<{ $collapsed?: boolean }>(
  ({ theme, $collapsed }) => ({
    width: $collapsed ? 80 : 280,
    minWidth: $collapsed ? 80 : 280,
    maxWidth: $collapsed ? 80 : 280,
    height: "100vh",
    background: `linear-gradient(180deg, ${tokens.primary.main} 0%, ${tokens.primary.light} 50%, ${tokens.secondary.main} 100%)`,
    color: theme.palette.common.white,
    borderRight: "none",
    display: "flex",
    flexDirection: "column",
    transition: "width 0.3s",
    zIndex: 1200,
  })
);

const Sidebar: React.FC<SidebarProps> = ({ open, collapsed, onClose }) => {
  const navigate = useNavigate();
  const location = useLocation();
  const { user } = useAuth();
  const isMobile = window.innerWidth < 900;

  // Get menu items based on user role
  const userRole = (user?.role as UserRole) || 'USER';
  const menuItems = getMenuItemsForRole(userRole);

  if (isMobile) {
    // Mobile: use Drawer
    return (
      <StyledDrawer
        variant="temporary"
        open={open}
        onClose={onClose}
        sx={{
          width: 280,
          "& .MuiDrawer-paper": {
            width: 280,
            transition: "width 0.3s",
          },
        }}
      >
        <Logo>
          <img src="/logo.png" alt="Bika Logo" />
        </Logo>
        <List>
          {menuItems.map((item) => {
            const IconComponent = getIconComponent(item.icon);
            return (
              <StyledListItem
                key={item.text}
                active={location.pathname === item.path ? 1 : 0}
                onClick={() => {
                  navigate(item.path);
                  onClose();
                }}
                sx={{ justifyContent: "flex-start", padding: "8px 16px" }}
              >
                <ListItemIcon
                  sx={{
                    color: "inherit",
                    minWidth: 40,
                    justifyContent: "center",
                  }}
                >
                  <IconComponent />
                </ListItemIcon>
                <ListItemText
                  primary={
                    <Typography variant="body1" fontWeight={500}>
                      {item.text}
                    </Typography>
                  }
                />
              </StyledListItem>
            );
          })}
        </List>
      </StyledDrawer>
    );
  }

  // Desktop: use Box as flex child
  return (
    <SidebarBox $collapsed={collapsed}>
      <Logo
        sx={{
          justifyContent: collapsed ? "center" : "flex-start",
          padding: collapsed ? "24px 0" : "24px 16px",
        }}
      >
        {!collapsed ? <img src="/logo.png" alt="Bika Logo" /> : null}
      </Logo>
      <List>
        {menuItems.map((item) => {
          const IconComponent = getIconComponent(item.icon);
          return (
            <StyledListItem
              key={item.text}
              active={location.pathname === item.path ? 1 : 0}
              onClick={() => navigate(item.path)}
              sx={{
                justifyContent: collapsed ? "center" : "flex-start",
                padding: collapsed ? "8px 0" : "8px 16px",
              }}
            >
              {collapsed ? (
                <Tooltip title={item.text} placement="right">
                  <ListItemIcon
                    sx={{
                      color: "inherit",
                      minWidth: 40,
                      justifyContent: "center",
                    }}
                  >
                    <IconComponent />
                  </ListItemIcon>
                </Tooltip>
              ) : (
                <ListItemIcon
                  sx={{
                    color: "inherit",
                    minWidth: 40,
                    justifyContent: "center",
                  }}
                >
                  <IconComponent />
                </ListItemIcon>
              )}
              {!collapsed ? (
                <ListItemText
                  primary={
                    <Typography variant="body1" fontWeight={500}>
                      {item.text}
                    </Typography>
                  }
                />
              ) : null}
            </StyledListItem>
          );
        })}
      </List>
    </SidebarBox>
  );
};

export default Sidebar;
