const ReportsPage: React.FC = () => {
  const [selectedReport, setSelectedReport] = useState<string | null>(null);

  const handleCardClick = (key: string) => setSelectedReport(key);
  const handleBack = () => setSelectedReport(null);

  return (
    <Box width="100%" boxSizing="border-box" p={3}>
      {/* Breadcrumbs */}
      <Breadcrumbs sx={{ mb: 2 }}>
        <Link
          component="button"
          underline="hover"
          color="primary"
          onClick={() => setSelectedReport(null)}
        >
          Home
        </Link>
        <Link
          component="button"
          underline="hover"
          color="primary"
          onClick={() => setSelectedReport(null)}
        >
          Reports
        </Link>
        {selectedReport && (
          <Typography color="text.primary">
            {reportList.find((r) => r.key === selectedReport)?.title}
          </Typography>
        )}
      </Breadcrumbs>
      {/* Dashboard or Detail */}
      {!selectedReport ? (
        <>
          <Typography variant="h4" fontWeight={600} mb={4}>
            Reports Dashboard
          </Typography>
          <Grid container spacing={3}>
            {reportList.map((report) => (
              <Grid item xs={12} sm={6} md={4} key={report.key}>
                <Card>
                  <CardActionArea onClick={() => handleCardClick(report.key)}>
                    <CardContent>
                      <Box display="flex" alignItems="center" gap={2} mb={1}>
                        {report.icon}
                        <Typography variant="h6">{report.title}</Typography>
                      </Box>
                      <Typography variant="body2" color="text.secondary">
                        {report.description}
                      </Typography>
                    </CardContent>
                  </CardActionArea>
                </Card>
              </Grid>
            ))}
          </Grid>
        </>
      ) : (
        <Box>
          <Box display="flex" alignItems="center" mb={2}>
            <IconButton onClick={handleBack}>
              <ArrowBackIcon />
            </IconButton>
            <Typography variant="h5" fontWeight={500} ml={1}>
              {reportList.find((r) => r.key === selectedReport)?.title}
            </Typography>
          </Box>
          <Paper sx={{ p: 3 }}>{mockDetails[selectedReport]}</Paper>
        </Box>
      )}
    </Box>
  );
};

export default ReportsPage;
