// Role-based access control utilities
// Role hierarchy - higher number means higher privilege
export const ROLE_HIERARCHY={'USER':1,'MANAGER':2,'COMPANY_ADMIN':3,'SUPER_ADMIN':4};// Check if user has a specific role
export const hasRole=(userRole,requiredRole)=>{if(!userRole)return false;return userRole===requiredRole;};// Check if user has any of the required roles
export const hasAnyRole=(userRole,requiredRoles)=>{if(!userRole)return false;return requiredRoles.includes(userRole);};// Check if user has minimum role level (equal or higher in hierarchy)
export const hasMinimumRole=(userRole,minimumRole)=>{if(!userRole)return false;const userLevel=ROLE_HIERARCHY[userRole];const minimumLevel=ROLE_HIERARCHY[minimumRole];return userLevel>=minimumLevel;};// Get user-friendly role display name
export const getRoleDisplayName=role=>{if(!role)return'User';switch(role){case'SUPER_ADMIN':return'Super Admin';case'COMPANY_ADMIN':return'Admin';case'MANAGER':return'Manager';case'USER':return'User';default:return role;}};// Check if user can access a specific route based on role
export const canAccessRoute=(userRole,routePath)=>{if(!userRole)return false;// Define route permissions
const routePermissions={'/dashboard':['SUPER_ADMIN','COMPANY_ADMIN','MANAGER','USER'],'/companies':['SUPER_ADMIN'],'/users':['SUPER_ADMIN','COMPANY_ADMIN'],'/departments':['SUPER_ADMIN','COMPANY_ADMIN'],'/document-types':['COMPANY_ADMIN','MANAGER','USER'],'/archival':['COMPANY_ADMIN','MANAGER','USER'],'/retrieval':['COMPANY_ADMIN','MANAGER','USER'],'/reports':['MANAGER'],'/drive':['COMPANY_ADMIN','MANAGER','USER'],'/settings':['SUPER_ADMIN','COMPANY_ADMIN','MANAGER','USER']};const allowedRoles=routePermissions[routePath];return allowedRoles?hasAnyRole(userRole,allowedRoles):false;};// Get menu items for a specific role
export const getMenuItemsForRole=(userRole,allMenuItems)=>{if(!userRole)return[];return allMenuItems.filter(item=>item.roles.includes(userRole));};
